import React from 'react'
import { loginSchema } from './login';
import {  } from 'react-hook-form';
import { useForm, NestedValue, SubmitHandler  } from 'react-hook-form';

type Props = {
    onSubmitForm: any
}
type Option = {
    label: string;
    value: string;
  };
  type FormValues = {
    firstName: string;
    lastName: string;
    email: string;
  };
  
export default function SignUp(props: Props) {

    // const { register, handleSubmit, setValue, reset } =
    //     useForm<loginSchema>();

    const { register, handleSubmit } = useForm<FormValues>();
  const onSubmit: SubmitHandler<FormValues> = data => console.log(data);

  return (
    <div className="login-form">
    <div className="title">Sign In</div>
    <div className="form">
        <form onSubmit={handleSubmit(props.onSubmitForm)}>
            <div className="input-container">
                <label>Username </label>
                <input
                    type="email"
                    className="form-control"
                    placeholder="Company Code"
                   
                    {...register("email")} 
                    // style={{ border: errors.code ? '1px solid red' : '' }}

                    // onChange={(e) => {
                    //     checkExistence(e.target.value);
                    //     codeLengthValidation(e.target.value);
                    // }}
                />
            </div>
            <div className="input-container">
                <label>Password </label>
                {/* <input type="password" name="pass" required /> */}
                {/* {renderErrorMessage("pass")} */}
            </div>
            <div className="button-container">
                <input type="submit" />
            </div>
        </form>
    </div>
</div>
  );
}
